import React, { useState } from 'react';
import Axios from 'axios';
import {Button,Modal,Table} from 'react-bootstrap'
class Listado extends React.Component {
    constructor(props) {
        super(props)
        
    }
    
    
   
    render (){
        const [propList, setPropList] = useState([])
        const [show, setShow] = useState(false);
        const [dataModal, setModal] = useState([]);
        const setDataModal = (val) => {
            setModal(val)
            setShow(true)
        }
        const getProspectos = () => {
               Axios.get("http://localhost:3002/listado").then((response)=>{
                console.log(response)
                   setPropList(response.data)
                   
               })
             
        }
    return(
        
        <div>
           
            <button onClick={getProspectos} value="xd">Data</button>
            
            <Modal show={show}
            onHide={() => setShow(false)} className="modal"> 
            <Modal.Header closeButton>

            </Modal.Header>
            <Modal.Body >
                <div className="modalBody">
                <div>
                <p className="labelBody">Estatus:</p>
                <p className="subBody">{dataModal.estatus}</p>
                <p className="labelBody">Nombre:</p>
                <p className="subBody">{dataModal.nombre}</p>
                <p className="labelBody">Primer Apellido:</p>
                <p className="subBody">{dataModal.primer_apellido}</p>
                <p className="labelBody">Segundo Apellido:</p>
                <p className="subBody">{dataModal.segundo_apellido}</p>
                <p className="labelBody">Segundo Apellido:</p>
                <p className="subBody">{dataModal.calle}</p>
                <p className="labelBody">Numero:</p>
                <p className="subBody">{dataModal.calle}</p>
                 </div>
                <hr/>
                <div className="documentContainer">
                <h1>
                            Documentos
                </h1>
                </div>
                   
                </div>
               
                
            </Modal.Body>
            </Modal>
            {propList.map((val,key)=>{
                return (
                    <div>
                         {val.estatus == "0"
                    
                    ?<h1>{val.nombre}</h1> 
                    :<h1>No</h1> 
                }
            <Table striped bordered hover>
                    <thead>
                        <tr>
                        <th>Nombre</th>
                        <th>Primer Apellido</th>
                        <th>Segundo Apellido</th>
                        <th>Estatus</th>
                        <th></th>
                        </tr>
                    </thead>
                    <tbody>
                        <tr>
                        <td>{val.nombre}</td>
                        <td>{val.primer_apellido}</td>
                        <td>{val.segundo_apellido}</td>
                        <td>{val.estatus == '0' ? 'Enviado' : 'Rechazado'}</td>
                        <td><Button onClick={()=>setDataModal(val)}>Ver Prospecto</Button></td>
                        </tr>
            
                    </tbody>
                    

                    </Table>
                    
               
                 </div>
                );
            })}
        </div>
    )
}
  
}

export default Listado;
