import './App.css';
import {useState} from "react";
import Axios from 'axios';
function App() {
  const [nombre,setNombre] = useState("")
  const [primerAp,setPrimerAp] = useState("")
  const [segundoAp,setSegundoAp] = useState("")
  const [calle,setCalle] = useState("")
  const [numero,setNumero] = useState("")
  const [colonia,setColonia] = useState("")
  const [codigoPostal,setCodigoPostal] = useState("")
  const [telefono,setTelefono] = useState("")
  const [rfc,setRfc] = useState("")
  const [documentos,setDocumentos] = useState([])

  const agregarProspecto = () => {
    Axios.post('http://localhost:3002/registrar',{
      nombre:nombre,
      primerApellido:primerAp,
      segundoApellido:segundoAp,
      calle:calle,
      numero:numero,
      colonia:colonia,
      codigoPostal:codigoPostal,
      telefono:telefono,
      rfc:rfc,
      documentos:documentos

    }).then(()=>{
      console.log('Success')
    })
  }
  console.log('RELAX: ',documentos)
  return (
    <div className="App">
      <header>
        <h1>Header</h1>
      </header>
      <div className="informacion">
     <label>Nombre</label>
     <input type="text" onChange={(e)=>{setNombre(e.target.value);
    }}/>
    <label>Primer Apellido</label>
     <input type="text" onChange={(e)=>{setPrimerAp(e.target.value);
    }}/>
    <label>Segundo Apellido</label>
     <input type="text" onChange={(e)=>{setSegundoAp(e.target.value);
    }}/>
    <label>Calle</label>
     <input type="text" onChange={(e)=>{setCalle(e.target.value);
    }}/>
    <label>Numero</label>
     <input type="text" onChange={(e)=>{setNumero(e.target.value);
    }}/>
     <label>Colonia</label>
     <input type="text" onChange={(e)=>{setColonia(e.target.value);
    }}/>
    <label>Codigo Postal</label>
      <input type="text" onChange={(e)=>{setCodigoPostal(e.target.value);
      }}/>
    <label>Telefono</label>
    <input type="text" onChange={(e)=>{setTelefono(e.target.value);
    }}/>
    <label>RFC</label>
    <input type="text" onChange={(e)=>{setRfc(e.target.value);
    }}/>
    <label>Documentos</label>
    <input type="file" multiple onChange={(e)=>{setDocumentos(e.target.files[0]);
    }}/>

    <button onClick={agregarProspecto}>Agregar Prospecto</button>
    </div>

    </div>
  );
}

export default App;
