import React, { useState } from 'react';
import Axios from 'axios';
import {Button,Modal,Table} from 'react-bootstrap'
const Listado = () => {
    
    
    const [propList, setPropList] = useState([])
    const [show, setShow] = useState(false);
    const [dataModal, setModal] = useState([]);

    const getProspectos = () => {
           Axios.get("http://localhost:3002/listado").then((response)=>{
            console.log(response)
               setPropList(response.data)
               
           })
         
    }
    
    return(
        
        <div>
            <button onClick={getProspectos} value="xd">Data</button>
            
            <Modal show={show}
            onHide={() => setShow(false)}> 
            <Modal.Header closeButton>

            </Modal.Header>
            <Modal.Body>
                {dataModal.nombre}
            </Modal.Body>
            </Modal>
            {propList.map((val,key)=>{
                return (
                    <div>
                         {val.estatus == "0"
                    
                    ?<h1>{val.nombre}</h1> 
                    :<h1>No</h1> 
                }
            <Table striped bordered hover>
                    <thead>
                        <tr>
                        <th>Nombre</th>
                        <th>Primer Apellido</th>
                        <th>Segundo Apellido</th>
                        <th>Estatus</th>
                        <th></th>
                        </tr>
                    </thead>
                    <tbody>
                        <tr>
                        <td>{val.nombre}</td>
                        <td>{val.primer_apellido}</td>
                        <td>{val.segundo_apellido}</td>
                        <td>{val.estatus == '0' ? 'Enviado' : 'Rechazado'}</td>
                        <td><Button onClick={() => setShow(true),()=>setModal(val)}>Ver Prospecto</Button></td>
                        </tr>
            
                    </tbody>
                    

                    </Table>
                    
               
                 </div>
                );
            })}
        </div>
    )
  
}

export default Listado;
